setTriggerStayOpen("venoms missing", 0)
local all_venoms = { "sumac", "xentio", "oleander", "eurypteria", "kalmia", "digitalis", "darkshade", "curare", "epteth", "prefarar", "monkshood", "euphorbia", "colocasia", "oculus", "hepafarin", "camus", "vernalius", "epseth", "larkspur", "slike", "voyria", "delphinium", "vardrax", "loki", "aconite", "selarnia", "gecko", "scytherus", "ouabain" }
local missing_venoms = {}
for venom in pairs(all_venoms) do if not table.contains(snd.my_venoms, all_venoms[venom]) then table.insert(missing_venoms, all_venoms[venom]) end end if #missing_venoms >= 1 then snd.message("You are missing: <red>"..table.concat(missing_venoms, "<white>,<red> ")) snd.send(" ") else snd.message("You have all the venoms!") snd.send(" ") end

Alias.script:
local room = matches[3] or mmp.currentroom room = tonumber(room) or mmp.relativeroom(mmp.currentroom, room) if not room or not mmp.roomexists(room) then mmp.echo("Sorry - which room do you want to put this character in? I don't know where you are at the moment, if you want to do the current room.") return end
local char = matches[2]
if char == "clear" then setRoomChar(room, ' ') mmp.echo("Cleared the character from "..room.." ("..getRoomName(room)..")") else setRoomChar(room, char) mmp.echo("Set the "..char:sub(1,1).." character on "..room.." ("..getRoomName(room)..")") end centerview(mmp.currentroom)

local continents = mmp.getcontinents()
if not next(continents) then mmp.echo("No continents known.") else for continent, areadata in pairs(continents) do mmp.echo(continent.." continent:")
for _, areaid in ipairs(areadata) do cecho(" "..getRoomAreaName(areaid).."\n") end end end

if (matches[2] and matches[2] == "on") or (not matches[2] and not mmp.editing) then mmp.editing = true enableAlias("mm Mapping")
mmp_regenerate_areas() mmp.highlight_unfinished_rooms()
mmp.echo("Mapping mode enabled. Happy mapping!") elseif (matches[2] and matches[2] == "off") or (not matches[2] and mmp.editing) then mmp.editing = false disableAlias("mm Mapping") mmp.echo("Mapping mode disabled.") end

mmp = mmp or {}
mmp.getPathPerf = mmp.getPathPerf or createStopWatch() startStopWatch(mmp.getPathPerf)
local from, to = tonumber(matches[2]), tonumber(matches[3])
getPath(from, to)
mmp.echon = mmp.echon or echo mmp.echon("a new getPath() from "..from.." to "..to.." took "..stopStopWatch(mmp.getPathPerf).."s. There are "..#speedWalkPath.." rooms to visit in it.") echo(" ") echoLink("[unhighlight]", [[ for room in pairs(mmp.getpathhighlights) do unHighlightRoom(room) end ]], "Click me to remove highlighting from getpath")
mmp.getpathhighlights = mmp.getpathhighlights or {}
for room in pairs(mmp.getpathhighlights) do unHighlightRoom(room) end
mmp.getpathhighlights = {}
local r,g,b = unpack(color_table.yellow) local br,bg,bb = unpack(color_table.yellow) -- add the first room to the speedWalkPath, as we'd like it highlighted as well table.insert(speedWalkPath, 1, from) for i = 1, #speedWalkPath do local room = speedWalkPath[i] highlightRoom(room, r,g,b,br,bg,bb, 1, 255, 255) mmp.getpathhighlights[room] = true end
centerview(from)

if snd.class == "Ascendril" then snd.starting_attack()
function snd.attack_function() if not snd.waiting.queue then local string = "" -- we'll load this with our attack string local call = "" -- and this with our affliction call -- next, the afflictions we want to give local touse = {} local tohit = {} local stalled = false if snd.defenses.def_stalled.state == "deffed" then stalled = true end -- and in case we don't find enough of them local total_aff_count = 0 for aff in pairs(snd.all_affs) do if table.contains(snd.target_has, snd.all_affs[aff]) then total_aff_count = total_aff_count + 1 end end local extraaff = "" snd.giving = {"anorexia"} for i in pairs(snd.giving) do -- make sure not to double up paresis and paralysis if not snd.checkAff(snd.giving[i]) then -- and don't add an aff we've already got in the list! if not table.contains(touse, snd.giving[i]) then table.insert(touse, snd.giving[i]) end end end -- now let's convert affs to venoms/empowerments for aff in pairs(touse) do table.insert(tohit, snd.effects[touse[aff]]) end -- if we didn't find enough stuff to hit with, stick in our filler while #tohit < 3 do table.insert(tohit, extraaff) end if snd.glimpse == "none" and not snd.glimpsing and not snd.checkAff("frostbrand") then string = "fulcrum glimpse water" elseif snd.glimpse == "none" and snd.checkAff("frostbrand") then string = "fulcrum catalyst " .. snd.target .. " frost" elseif snd.used.shield == true or ((snd.checksomeAffs({"a broken left leg", "a broken right leg"},1) or snd.limb_status["left leg"] ~= "healed" or snd.limb_status["right leg"] ~= "healed") and not snd.target_prone) then string = "cast windlance " .. snd.target elseif snd.glimpse == "water" or snd.glimpse == "fire" or snd.glimpse == "air" then if gmcp.Char.Vitals.ability_bal == "1" then string = "fulcrum flare " .. snd.target end if snd.checkAff("frozen") and snd.checkAff("impaled") then string = string .. snd.sep .. "cast shatter " .. snd.target elseif gmcp.Char.Vitals.resonance == "water" then if not stalled then if snd.limb_dmg.torso == 0 and snd.parried_limb ~= "torso" then string = string .. snd.sep .. "fulcrum stall 3 firelash " .. snd.target .. " torso" elseif snd.limb_dmg.torso > 0 then string = string .. snd.sep .. "fulcrum stall 3 mistral " .. snd.target end elseif stalled then if snd.checkAff("shivering") and not snd.checkAff("direfrost") and not snd.checkAff("frostbrand") then string = string .. snd.sep .. "cast direfrost " .. snd.target elseif snd.checkAff("frozen") and not snd.target_impaled then string = string .. snd.sep .. "cast icicle " .. snd.target else string = string .. snd.sep .. "cast mistral " .. snd.target end end elseif gmcp.Char.Vitals.resonance == "fire" then if not stalled then if snd.limb_dmg.torso == 0 and snd.parried_limb ~= "torso" then string = string .. snd.sep .. "fulcrum stall 3 firelash " .. snd.target .. " torso" elseif snd.limb_dmg.torso > 0 then string = string .. snd.sep .. "fulcrum stall 3 coldsnap " .. snd.target end elseif stalled then if snd.checkAff("shivering") and not snd.checkAff("direfrost") and not snd.checkAff("frostbrand") then string = string .. snd.sep .. "cast direfrost " .. snd.target elseif snd.checkAff("frozen") and not snd.target_impaled then string = string .. snd.sep .. "cast icicle " .. snd.target elseif not snd.checkAff("gorged") then string = string .. snd.sep .. "cast gorge " .. snd.target .. " euphoriant" else string = string .. snd.sep .. "cast hailstorm " .. snd.target end end elseif gmcp.Char.Vitals.resonance == "air" then if not stalled then if snd.limb_dmg.torso == 0 and snd.parried_limb ~= "torso" then string = string .. snd.sep .. "fulcrum stall 3 firelash " .. snd.target .. " torso" else string = string .. snd.sep .. "fulcrum stall 3 hailstorm " .. snd.target end elseif stalled then if snd.checkAff("shivering") and not snd.checkAff("direfrost") and not snd.checkAff("frostbrand") then string = string .. snd.sep .. "cast direfrost " .. snd.target elseif snd.checkAff("frozen") and not snd.target_impaled then string = string .. snd.sep .. "cast icicle " .. snd.target elseif not snd.checkAff("gorged") then string = string .. snd.sep .. "cast gorge " .. snd.target .. " euphoriant" elseif not snd.checkAff("masochism") then string = string .. snd.sep .. "cast coldsnap " ..snd.target else string = string .. snd.sep .. "cast hailstorm " ..snd.target end end end end if string ~= snd.last_attack and not snd.waiting.queue then snd.last_attack = string snd.attack(snd.last_attack) snd.waiting.queue = true tempTimer(snd.delay(), [[snd.waiting.queue = false]]) end end end
snd.attack_function() end

if snd.class == "Ascendril" then snd.starting_attack()
function snd.attack_function() if not snd.waiting.queue then local string = "" -- we'll load this with our attack string local call = "" -- and this with our affliction call local limbs = {"left leg", "right leg", "left arm", "right arm"} local targ = "nothing" local stalled = false if snd.defenses.def_stalled.state == "deffed" then stalled = true end for limb in pairs(limbs) do -- ignore last parried, last hit, and any limbs being resto'd if limbs[limb] ~= snd.parried_limb and limbs[limb] ~= snd.last_hit_limb and limbs[limb] ~= snd.resto_limb then if targ == "nothing" then targ = limbs[limb] end end end if snd.glimpse == "none" and not snd.glimpsing and not snd.checkAff("frostbrand") then string = "fulcrum glimpse water" elseif snd.glimpse == "none" and snd.checkAff("frostbrand") then string = "fulcrum catalyst " .. snd.target .. " frost" elseif snd.used.shield == true then string = "cast windlance " .. snd.target elseif snd.glimpse == "water" or snd.glimpse == "fire" or snd.glimpse == "air" then if gmcp.Char.Vitals.ability_bal == "1" then string = "fulcrum flare " .. snd.target end if snd.checkAff("frozen") and snd.checkAff("impaled") then string = string .. snd.sep .. "cast shatter " .. snd.target elseif gmcp.Char.Vitals.resonance == "water" then if not stalled then if snd.limb_dmg.torso == 0 and snd.parried_limb ~= "torso" then string = string .. snd.sep .. "fulcrum stall 3 firelash " .. snd.target .. " torso" else string = string .. snd.sep .. "fulcrum stall 3 firelash " .. snd.target .. " " .. targ end elseif stalled then if snd.checkAff("no_caloric") and not snd.checkAff("direfrost") and not snd.checkAff("frostbrand") then string = string .. snd.sep .. "cast direfrost " .. snd.target elseif snd.checkAff("frozen") and not snd.target_impaled then string = string .. snd.sep .. "cast icicle " .. snd.target else string = string .. snd.sep .. "cast firelash " .. snd.target .. " " .. targ end end elseif gmcp.Char.Vitals.resonance == "fire" then if not stalled then if snd.limb_dmg.torso == 0 and snd.parried_limb ~= "torso" then string = string .. snd.sep .. "fulcrum stall 3 firelash " .. snd.target .. " torso" else string = string .. snd.sep .. "fulcrum stall 3 firelash " .. snd.target .. " " .. targ end elseif stalled then if snd.checkAff("no_caloric") and not snd.checkAff("direfrost") and not snd.checkAff("frostbrand") then string = string .. snd.sep .. "cast direfrost " .. snd.target elseif snd.checkAff("frozen") and not snd.target_impaled then string = string .. snd.sep .. "cast icicle " .. snd.target else string = string .. snd.sep .. "cast firelash " .. snd.target .. " " .. targ end end elseif gmcp.Char.Vitals.resonance == "air" then if not stalled then if snd.limb_dmg.torso == 0 and snd.parried_limb ~= "torso" then string = string .. snd.sep .. "fulcrum stall 3 firelash " .. snd.target .. " torso" else string = string .. snd.sep .. "fulcrum stall 3 firelash " .. snd.target .. " " .. targ end elseif stalled then --not snd.direfrost and not snd.checkAff("frostbrand") then if snd.checkAff("no_caloric") and not snd.checkAff("direfrost") and not snd.checkAff("frostbrand") then string = string .. snd.sep .. "cast direfrost " .. snd.target elseif snd.checkAff("frozen") and not snd.target_impaled then string = string .. snd.sep .. "cast icicle " .. snd.target else string = string .. snd.sep .. "cast firelash " .. snd.target .. " " .. targ end end end end if string ~= snd.last_attack and not snd.waiting.queue then snd.last_attack = string snd.attack(snd.last_attack) snd.waiting.queue = true tempTimer(snd.delay(), [[snd.waiting.queue = false]]) end end end
snd.attack_function() end

if snd.class == "Templar" then snd.starting_attack() function snd.attack_function() if not snd.waiting.queue then local string = "" -- we'll load this with our attack string local call = "" -- and this with our affliction call -- next, the afflictions we want to give local touse = {} local tohit = {} -- and in case we don't find enough of them local extraaff = "hemorrhage" local iceblast = false if tonumber(gmcp.Char.Vitals.charge_left) >= 100 and tonumber(gmcp.Char.Vitals.charge_right) >= 100 then iceblast = true end -- set the affs we want to use snd.giving = { "a broken right leg", "a broken left leg", "a broken right arm", "a broken left arm", "stupidity", "mental disruption", "anorexia", "slickness", "asthma", "paresis", "crippled", "physical disruption", }
-- pick affs from the list above for i in pairs(snd.giving) do -- make sure not to double up paresis and paralysis if not snd.checkAff(snd.giving[i]) then -- and don't add an aff we've already got in the list! if not table.contains(touse, snd.giving[i]) then table.insert(touse, snd.giving[i]) end end end
-- first and foremost, check if we can retri! if snd.checkAff("paralysis") and snd.checkAff("crippled body") and snd.checkAff("mental disruption") and snd.checkAff("physical disruption") then string = "retribution "..snd.target elseif snd.target_impaled then string = "disembowel "..snd.target elseif snd.proned() and not snd.checkAff("impaled") then string = "penance "..snd.target..snd.sep.."impale "..snd.target if iceblast then string = "blade release left iceblast "..snd.target..snd.sep.."blade release right iceblast "..snd.target..snd.sep..string end else -- now let's convert affs to venoms/empowerments for aff in pairs(touse) do table.insert(tohit, snd.effects[touse[aff]]) end -- if we didn't find enough stuff to hit with, stick in our filler while #tohit < 3 do table.insert(tohit, extraaff) end -- now to decide what attack to use! if snd.used.shield then string = "raze "..snd.target elseif snd.used.rebounding then string = "razestrike "..snd.target.." "..tohit[1] snd.blazing = true call = "wt Afflicting "..snd.target..": Blaze, "..tohit[1]..snd.sep elseif iceblast then string = "blade release right iceblast "..snd.target..snd.sep.."blade release left iceblast "..snd.target..snd.sep.."dsk "..snd.target.." "..tohit[2].." "..tohit[1] call = "wt Afflicting "..snd.target..": "..tohit[2]..", "..tohit[1]..snd.sep else string = "dsk "..snd.target.." "..tohit[2].." "..tohit[1] call = "wt Afflicting "..snd.target..": "..tohit[2]..", "..tohit[1]..snd.sep end end -- prepend our affliction call, if we need one if snd.toggles.affcalling and not snd.target_gone then string = call..string end -- handle anti-spam and the actual attacking now if string ~= snd.last_attack and not snd.waiting.queue then snd.last_attack = string snd.attack(snd.last_attack) snd.waiting.queue = true tempTimer(snd.delay(), [[snd.waiting.queue = false]]) end end end snd.attack_function() end

