if snd.class == "Zealot" then snd.starting_attack()
function snd.attack_function() -- This checks for a present Firefist defense. This is used later during a Firefist check. local firefist = false if snd.defenses.def_firefist.state == "deffed" then firefist = true end -- This checks for a present Zenith defense. It is used later in Zenith/Flow Handling. local zenith = false if snd.defenses.def_zenith.state == "deffed" then zenith = true end -- This is a failsafe to take crispy off of a target that has cured ablaze. if snd.checkAff("crispy") then if not snd.checkaff("ablaze") then snd.target_cured("crispy") end end -- This uses crispy to check if they're at the right flame level. local immolation_ready = false if snd.checkAff("ablaze") then if snd.checkAff("crispy") then immolation_ready = true end end -- In this mode, we're going to only rely on Purification. It's built to support a quick Immolate. if not snd.waiting.queue then if snd.used.shield then string = "touch hammer " .. snd.target -- OK, so if immolation is ready... we should cast it! elseif immolation_ready then string = "enact immolation " .. snd.target -- OK, so if immolation isn't ready, what next? Heatspear! elseif not snd.checkAff("heatspear") and snd.checkAff("ablaze") then string = "enact heatspear " .. snd.target -- OK, what if they have heatspear? Are they ablaze?! Quicken! -- elseif snd.checkAff("ablaze") then -- string = "enact quicken "..snd.target -- OK, so we've done all of this, but they don't have ablaze! Ez fix.. else string = "enact scorch " .. snd.target end -- Flow Handling/Zenith Handling -- In this mode, we only Flow when we have Zenith, otherwise we are casting. -- This means our Flow handling IS our Zenith handling. -- Our Flow attacks should always be punches to take advantage of Firefist. if zenith then if snd.used.shield then string = "touch hammer " .. snd.target elseif snd.parried_limb ~= "torso" then string = "flow " .. snd.target .. " clawtwist clawtwist" .. snd.sep .. string elseif snd.parried_limb ~= "left arm" then string = "flow " .. snd.target .. " pummel left pummel left" .. snd.sep .. string else string = "flow " .. snd.target .. " pummel right pummel right" .. snd.sep .. string end end -- Hackles handling -- In this mode, we really just hackles whipburst. -- We will Whipburst even in lieu of a Flow combo. -- First, we should check if we have hackles balance. if gmcp.Char.Vitals.ability_bal == "1" then string = "hackles " .. snd.target .. " whipburst " .. snd.sep .. string end end -- We want to turn on firefist if we have Zenith up, because that's the only time we're punching and it needs to count. if not firefist then if snd.checksomeAffs({"ablaze", "heatspear"}, 2) then if zenith then string = "enact firefist" else string = "enact zenith" .. snd.sep .. string end end end -- I am leaving this failsafe in... below checks if we're g2g with the immolate button. if immolation_ready then string = "enact immolation " .. snd.target end if string ~= snd.last_attack and not snd.waiting.queue then snd.last_attack = string snd.attack(snd.last_attack) snd.waiting.queue = true tempTimer(snd.delay(), [[snd.waiting.queue = false]]) end end
snd.attack_function() end

if snd.class == "Zealot" then snd.starting_attack()
function snd.attack_function() local firefist = false if snd.defenses.def_firefist.state == "deffed" then firefist = true end local zenith = false if snd.defenses.def_zenith.state == "deffed" then zenith = true end if snd.checkAff("crispy") then if not snd.checkaff("ablaze") then snd.target_cured("crispy") end end local immolation_ready = false if snd.checkAff("ablaze") then if snd.checkAff("crispy") then immolation_ready = true end end -- Flow Handling -- Consider writing a check for different combos on firefist? if not snd.waiting.queue then if snd.used.shield then string = "touch hammer " .. snd.target elseif snd.parried_limb ~= "torso" then string = "flow " .. snd.target .. " clawtwist clawtwist" elseif snd.parried_limb ~= "head" then if not snd.checkAff("crippled_throat") then string = "flow " .. snd.target .. " edgekick heelrush head" else string = "flow " .. snd.target .. " sunkick heelrush head" end elseif snd.parried_limb ~= "left leg" or snd.parried_limb ~= "right leg" then string = "flow " .. snd.target .. " wanekick left wanekick right" elseif snd.parried_limb ~= "left leg" and snd.parried_limb == "right leg" then string = "flow " .. snd.target .. " wanekick left wanekick left" elseif snd.parried_limb ~= "right leg" and snd.parried_limb == "left leg" then string = "flow " .. snd.target .. " wanekick right wanekick right" else if snd.parried_limb ~= "left arm" then string = "flow " .. snd.target .. " pummel left pummel left" end end -- Hackles handling -- Jawcrack > Rive ... rive to maximize the bleeding. -- Prioritize Whipburst during firefist? -- First, we should check if we have hackles balance. if gmcp.Char.Vitals.ability_bal == "1" then -- Cool! What are we doing with it? Let's figure that out and put it before our flow string! if snd.parried_limb ~= "head" and not snd.checkAff("blurry vision") then string = "hackles " .. snd.target .. " jawcrack " .. snd.sep .. string else string = "hackles " .. snd.target .. " rive " .. snd.sep .. string end end -- Zenith handling -- Immolation > Pyromania > Infernal > Heatspear > Quicken > Scorch -- handle pyromania with a variable? -- use the limb state checker from templar limbs script for infernal check? -- handle heatspear as an aff.. -- check if immolate not possible ... if not, quicken or scorch if zenith then if not snd.checkAff("heatspear") and snd.checkAff("ablaze") then string = string .. snd.sep .. "enact heatspear " .. snd.target elseif snd.checkAff("ablaze") then string = string .. snd.sep .. "enact quicken " .. snd.target else string = string .. snd.sep .. "enact scorch " .. snd.target end end if not firefist then if snd.checksomeAffs({"ablaze", "heatspear"}, 2) then string = "enact firefist" end end if immolation_ready then string = "enact immolation " .. snd.target end if string ~= snd.last_attack and not snd.waiting.queue then snd.last_attack = string snd.attack(snd.last_attack) snd.waiting.queue = true tempTimer(snd.delay(), [[snd.waiting.queue = false]]) end end end
snd.attack_function() end

if snd.class == "Indorani" then snd.giving_sun = { "paresis", "asthma", "sensitivity", "clumsiness", "vomiting", "lethargy", } snd.giving_moon = { "impatience", "stupidity", "confusion", "recklessness", "anorexia", "epilepsy", } snd.starting_attack()
function snd.attack_function()
local string = "" local sun = "none" local moon = "none"
if not snd.waiting.queue then if snd.used.shield then string = "touch hammer "..snd.target elseif snd.need_leech_aura == true then string = "leech "..snd.target else if snd.balance.sun then for i in pairs(snd.giving_sun) do if not snd.checkAff(snd.giving_sun[i]) and sun == "none" then sun = snd.giving_sun[i] end end end if snd.balance.moon then for i in pairs(snd.giving_moon) do if not snd.checkAff(snd.giving_moon[i]) and moon == "none" then moon = snd.giving_moon[i] end end end if hasSkill("Imprint") then if sun ~= "none" and moon ~= "none" then string = string.."outc blank as sun"..snd.sep.."outc blank as moon"..snd.sep.."fling sun "..sun.." and moon "..moon..snd.target..snd.sep end else if hasSkill("Sun") then string = string.."outc sun"..snd.sep.."fling sun "..sun..snd.target..snd.sep end end end if string ~= snd.last_attack and not snd.waiting.queue then snd.last_attack = string snd.attack(snd.last_attack) snd.waiting.queue = true tempTimer(snd.delay(), [[snd.waiting.queue = false]]) end end end snd.attack_function() end

if snd.class == "Praenomen" then snd.starting_attack() function snd.attack_function() local string = "" local touse = {} snd.giving_whisper = { "impatience", "loneliness", "stupidity", "anorexia", "confusion", "recklessness", "agoraphobia", "vertigo", "indifference", "peace", "masochism", "paranoia",
} for i in pairs(snd.giving_whisper) do if not snd.checkAff(snd.giving_whisper[i]) then if not table.contains(touse, snd.giving_whisper[i]) then table.insert(touse, snd.giving_whisper[i]) end end end
if not snd.waiting.queue then string = "frenzy "..snd.target..snd.sep.."whisper "..touse[1].." "..touse[2].." "..snd.target end
if string ~= snd.last_attack and not snd.waiting.queue then snd.last_attack = string snd.attack(snd.last_attack) snd.waiting.queue = true tempTimer(snd.delay(), [[snd.waiting.queue = false]]) end end snd.attack_function() end

Script.script:
registerAnonymousEventHandler("mmp link externals", "mmp.addWingsImperian") mmp.imperian = mmp.imperian or {}
function mmp.addWingsImperian() local function getcmd(word) return mmp.settings.removewings and [[script:if mmp.flying then sendAll("queue eqbal wear wings","queue eqbal say ]] .. word .. [[","queue eqbal remove wings",false) else sendAll("wear wings","fly",false) end]] or [[script:if mmp.flying then sendAll("queue eqbal say ]] .. word .. [[",false) else sendAll("fly",false) end]] end
if (mmp.settings.shekinah or mmp.settings.suriel) and gmcp.Room and not table.contains(gmcp.Room.Info.details, "indoors") and not table.contains(gmcp.Room.Info.details, "considered indoors") and table.contains(mmp.imperian.wingAbleAreas, getRoomArea(mmp.currentroom)) then if mmp.settings.shekinah then -- Seraphim wings - "Say shekinah" local weight = 15 if mmp.flying then weight = 1 end mmp.tempSpecialExit(4882, getcmd("shekinah"), weight) elseif mmp.settings.suriel then -- Using Orphanim wings, SURIEL works, takes you to 3885 local weight = 15 if mmp.flying == "1" then weight = 1 end mmp.tempSpecialExit(3885, getcmd("suriel"), weight) -- clear cache so mmp.getPath accounts for the new way end end mmp.clearpathcache() end
